import React from "react";

export function getIcon(type: string) {
  switch (type) {
    case "Country":
      return <LocationsIcon />;
    case "Partner":
      return <PartnersIcon />;
    case "Donor":
      return <DonorsIcon />;
    case "Grant":
      return <GrantsIcon />;
    case "Result":
      return <ResultsIcon />;
    case "Document":
      return <DocumentsIcon />;
    default:
      return <AllCategoriesIcon />;
  }
}

